mapper.select:
select a.*,b.total_trade_num,b.buy_num,b.sell_num from ( select stock_code, fund_pool_code, max(stock_name) as stock_name, sum(ifnull(recent_market_value,0)) as recent_market_value, sum(ifnull(profit_loss_value,0)) as profit_loss_value, sum(ifnull(total_volume,0)) as total_volume, sum(ifnull(available_sell_volume,0)) as available_sell_volume, sum(ifnull(total_volume,0)*cost_price)/sum(ifnull(total_volume,0)) as cost_price, max(current_price) as current_price from tcdb.t_securities_trader_hold where data_date=current_date()
group by stock_code,fund_pool_code ) a left join ( select tmp.stock_code, tmp.fund_pool_code, max(tmp.stock_name) as stock_name, sum(ifnull(tmp.buy_num,0)) as buy_num, sum(ifnull(tmp.sell_num,0)) as sell_num, sum(ifnull(tmp.buy_num,0)+ifnull(tmp.sell_num,0)) as total_trade_num from ( select fund_pool_code,stock_code,stock_name,case when bs_flag='0' then transaction_vloume else 0 end as buy_num,case when bs_flag='1' then transaction_vloume else 0 end as sell_num from tcdb.t_securities_trader_entrust where data_date=current_date()
)tmp group by tmp.stock_code,tmp.fund_pool_code ) b on a.stock_code=b.stock_code and a.fund_pool_code=b.fund_pool_code

select sum(a.recent_market_value ) as recent_market_value, sum(a.profit_loss_value ) as profit_loss_value, sum(a.total_volume) as total_volume, sum(a.available_sell_volume ) as available_sell_volume, sum(b.total_trade_num ) as total_trade_num, sum(b.buy_num ) as buy_num, sum(b.sell_num) as sell_num , count(*) as total_num from ( select stock_code, fund_pool_code, max(stock_name) as stock_name, sum(ifnull(recent_market_value,0)) as recent_market_value, sum(ifnull(profit_loss_value,0)) as profit_loss_value, sum(ifnull(total_volume,0)) as total_volume, sum(ifnull(available_sell_volume,0)) as available_sell_volume, sum(ifnull(total_volume,0)*cost_price)/sum(ifnull(total_volume,0)) as cost_price, max(current_price) as current_price from tcdb.t_securities_trader_hold where data_date=current_date()
group by stock_code,fund_pool_code ) a left join ( select tmp.stock_code, tmp.fund_pool_code, max(tmp.stock_name) as stock_name, sum(ifnull(tmp.buy_num,0)) as buy_num, sum(ifnull(tmp.sell_num,0)) as sell_num, sum(ifnull(tmp.buy_num,0)+ifnull(tmp.sell_num,0)) as total_trade_num from ( select fund_pool_code,stock_code,stock_name,case when bs_flag='0' then transaction_vloume else 0 end as buy_num,case when bs_flag='1' then transaction_vloume else 0 end as sell_num from tcdb.t_securities_trader_entrust where data_date=current_date()
)tmp group by tmp.stock_code,tmp.fund_pool_code ) b on a.stock_code=b.stock_code and a.fund_pool_code=b.fund_pool_code

select sum(a.market_value) as market_value, sum(a.profit_loss_value ) as profit_loss_value, sum(a.total_volume) as total_volume, sum(a.available_sell_volume ) as available_sell_volume, sum(b.total_trade_num ) as total_trade_num, sum(b.buy_num ) as buy_num, sum(b.sell_num ) as sell_num, sum(b.jmr_num ) as jmr_num, count(*) as total_num from ( select data_date, fund_pool_code, stock_code, max(stock_name) as stock_name, max(current_price) as current_price, sum(ifnull(recent_market_value,0)) as market_value , sum(ifnull(profit_loss_value,0)) as profit_loss_value, sum(ifnull(total_volume,0)) as total_volume, sum(ifnull(available_sell_volume,0)) as available_sell_volume, sum(ifnull(total_volume,0)*cost_price)/sum(ifnull(total_volume,0)) as cost_price from tcdb.t_securities_trader_hold where 1=1
group by data_date,fund_pool_code,stock_code ) a left join ( select tmp.data_date, tmp.fund_pool_code, tmp.stock_code, sum(ifnull(tmp.buy_num,0)) as buy_num, sum(ifnull(tmp.sell_num,0)) as sell_num, sum(ifnull(tmp.buy_num,0)+ifnull(tmp.sell_num,0)) as total_trade_num, sum(ifnull(tmp.buy_num,0)-ifnull(tmp.sell_num,0)) as jmr_num from ( select data_date,fund_pool_code,stock_code,stock_name,case when bs_flag='0' then transaction_vloume else 0 end as buy_num,case when bs_flag='1' then transaction_vloume else 0 end as sell_num from tcdb.t_securities_trader_entrust where 1=1
)tmp group by tmp.data_date,tmp.fund_pool_code,tmp.stock_code ) b on a.data_date=b.data_date and a.fund_pool_code=b.fund_pool_code and a.stock_code=b.stock_code
